import React, { useContext } from "react";
import taskContext from "../context/taskContext";

const EditTask = (props) => {
  const context = useContext(taskContext);

  const { task, updateTask } = props;

  //split the description into paragraph based on line breaks
  const paragraphs = task.description.split(/\r?\n/);
  return (
    <div className="container col-md-5">
      <div className="card my-3">
        <div className="card-body">
          <div className="d-flex align-items-center ">
            <h5 className="card-title">{task.title}</h5>

            <i
              className="fa-solid fa-pen-to-square mx-3"
              onClick={() => {
                updateTask(task);
              }}
            ></i>
          </div>

          {/* Map over the paragraphs array and create a <p> element for each */}
          {paragraphs.map((paragraph, index) => (
            <p key={index} className="card-text text-wrap">
              {paragraph}
            </p>
          ))}

          <button>Contact Us</button>
        </div>
      </div>
    </div>
  );
};

export default EditTask;
